import "./NewsCardSmall.css";

import { Button, Highlight } from "@mantine/core";

import { Card } from "react-bootstrap";
import CategoryBadge from "../Badge/CategoryBadge";
import DatePublished from "../DatePublished/DatePublished";
import React from "react";

const NewsCardSmall = ({ blog, query, setCategory, handlePost }) => {
  return (
    <Card className="blog-card">
      {blog.cloudinaryId ? <Card.Img variant="top" src={blog.image} /> : null}
      <Card.Body>
        <h1>
          <Highlight highlight={query}>{blog.title}</Highlight>
        </h1>
        <div className="createdAt">
          <DatePublished blog={blog} />
          <CategoryBadge
            blog={blog}
            onClick={() => setCategory(blog.category)}
          />
        </div>
        <div className="blog-items">
          <Button
            className="readMore"
            onClick={() => {
              handlePost(blog._id);
            }}
            variant="light"
            compact
          >
            Read More
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default NewsCardSmall;
