import { Notification } from "@mantine/core";
import React from "react";

const Notify = ({ notify, title, children }) => {
  return (
    <div
      style={{
        display: notify === true ? "flex" : "none",
        top: "14%",
        right: "2%",
        width: "19.2rem",
        position: "fixed",
      }}
    >
      <Notification color="teal" title={title} style={{ width: "100%" }}>
        {children}
      </Notification>
    </div>
  );
};

export default Notify;
