import { Confirm } from "react-st-modal";

const ConfirmationDialog = ({ handleDelete }) => {
  return (
    <div>
      <button
        onClick={async () => {
          const result = await Confirm(
            "Are you sure you want to delete this post?",
            "Delete"
          );

          if (result) {
            console.log("confirmed");
          } else {
            console.log("!confirmed");
          }
        }}
      >
        Yes
      </button>
    </div>
  );
};

export default ConfirmationDialog;
