import "./NewsCardSmall.css";

import { Button, Highlight, Text } from "@mantine/core";

import { Card } from "react-bootstrap";
import CategoryBadge from "../Badge/CategoryBadge";
import DatePublished from "../DatePublished/DatePublished";
import React from "react";

const NewsCardSmall = ({
  blog,
  query,
  handlePost,
  handleEdit,
  setCategory,
  handleDelete,
  profile = false,
}) => {
  return (
    <Card className="blog-card">
      {blog.cloudinaryId ? (
        <Card.Img
          variant="top"
          src={blog.image}
          onClick={() => (profile ? handlePost(blog._id) : null)}
        />
      ) : null}
      <Card.Body>
        <h1 onClick={() => (profile ? handlePost(blog._id) : null)}>
          <Highlight className="post-title" highlight={query}>
            {blog.title}
          </Highlight>
        </h1>

        <div className="card-body-bottom">
          <div className="created-at">
            <DatePublished blog={blog} />
            <span className="divider"></span>
            <CategoryBadge blog={blog} color="#333" onClick={setCategory} />
          </div>
          <div className="blog-items">
            {profile ? (
              <>
                <Button
                  className="editBtn"
                  onClick={() => {
                    handleEdit(blog._id);
                  }}
                >
                  Edit
                </Button>
                <Button
                  className="deleteBtn"
                  onClick={() => {
                    handleDelete(blog._id);
                  }}
                >
                  Delete
                </Button>
              </>
            ) : (
              <Button
                className="read-more"
                onClick={() => {
                  handlePost(blog._id);
                }}
                variant="light"
                compact
              >
                Read More
              </Button>
            )}
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default NewsCardSmall;
